-- SQLite
CREATE TABLE users (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, username TEXT NOT NULL, hash TEXT NOT NULL);
CREATE TABLE employees (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, username TEXT NOT NULL, hash TEXT NOT NULL, admin NUMERIC NOT NULL);
CREATE TABLE bookings (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, username TEXT NOT NULL, type TEXT NOT NULL, duration NUMERIC NOT NULL, day NUMERIC NOT NULL, month NUMERIC NOT NULL, year NUMERIC NOT NULL, start_time_hour NUMERIC NOT NULL, start_time_minute NUMERIC NOT NULL, finish_time_hour NUMERIC NOT NULL, finish_time_minute NUMERIC NOT NULL);
CREATE TABLE shop_items (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, item_name TEXT NOT NULL, price_unit NUMERIC NOT NULL, availability NUMERIC NOT NULL, link TEXT NOT NULL);
CREATE TABLE transactions (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, user_id INTEGER NOT NULL, item_id INTEGER NOT NULL, amount_items NUMERIC NOT NULL, total_price NUMERIC NOT NULL, paid NUMERIC NOT NULL, day NUMERIC NOT NULL, month NUMERIC NOT NULL, year NUMERIC NOT NULL, time CURRENT_TIMESTAMP NOT NULL);